(107, NULL, 1, 0, 10, NULL, 3, 'TIẾP TỤC LÔ HÀNG SERUM  MỚI CHO KHÁCH', '', '<h2>Ngày nào cũng thế:&nbsp;<a href="https://wicbe.com/serum-wicbe.html">Serum Dưỡng Trắng Da</a> luôn được đóng thùng để giao cho khách yêu.</h2>\n\n<p><img alt="" src="https://gelruamatwicbe.com/uploads/Images/z1033675586183_dbcb71e0fe8c3fe562da07fe0ceb319f.jpg" style="height:375px; width:500px" /></p>\n\n<p><img alt="SERUM DƯỠNG TRẮNG" src="https://wicbe.com/uploads/Files/tiep-tuc-lo-hang-serum-moi-cho-khach(1).jpg" style="height:375px; width:500px" /></p>\n\n<p><img alt="SERUM DƯỠNG TRẮNG" src="https://wicbe.com/uploads/Files/tiep-tuc-lo-hang-serum-moi-cho-khach(2).jpg" style="height:375px; width:500px" />&nbsp;&nbsp;</p>\n\n<p><img alt="SERUM DƯỠNG TRẮNG" src="https://wicbe.com/uploads/Files/tiep-tuc-lo-hang-serum-moi-cho-khach.jpg" style="height:667px; width:500px" /></p>\n\n<p>&nbsp;</p>\n\n<h5><img alt="" class="img" src="httpss://static.xx.fbcdn.net/images/emoji.php/v9/f72/1/16/1f538.png" style="height:16px; width:16px" /></h5>\n', 'tiep-tuc-lo-hang-serum-moi-cho-khach', 'TIẾP TỤC LÔ HÀNG SERUM  MỚI CHO KHÁCH', 'serum dưỡng trắng da', 'TIẾP TỤC LÔ HÀNG SERUM  MỚI CHO KHÁCH', 50, '1534732903', 'uploads/san-pham/gPiw1zzd3T0aEVKwvBdUE1ItRKdCzBdzdxjj61PjcTI2se0lG.jpg'),
(108, NULL, 1, 1, 0, 6, 2, 'Đánh giá', '', '<p><span style="color:rgb(96, 95, 95); font-family:arial; font-size:14px">Serum dưỡng trắng da chống lão hóa, chống tia ánh sáng xanh Wicbe với dưỡng chất chiết xuất từ 7 loại thảo mộc thiên nhiên ở độ cao 800m-1600m trên dãy núi Anpơ của Thụy Sĩ có tác dụng dưỡng trắng hồng da tự nhiên giúp cho làn da của bạn luôn trẻ trung, trắng hồng và rạng ngời.</span></p>\n', 'danh-gia', '', '', '', 0, '1531902454', 'uploads/san-pham/danh-gia-WG1.jpg'),
